const https = require('https');
const url = require('url');

exports.handler = async (event) => {
  console.log('AWS Exports Generator Event:', JSON.stringify(event, null, 2));

  try {
    const {
      USER_POOL_ID,
      USER_POOL_CLIENT_ID,
      GRAPHQL_API_URL,
      GRAPHQL_API_ID,
      APP_NAME,
      STAGE,
      MODELS
    } = process.env;

    // AWS_REGION is automatically provided by Lambda runtime
    const AWS_REGION = process.env.AWS_REGION;

    if (!USER_POOL_ID || !USER_POOL_CLIENT_ID || !GRAPHQL_API_URL || !AWS_REGION) {
      throw new Error('Missing required environment variables');
    }

    // Parse models from environment variable
    const models = MODELS ? JSON.parse(MODELS) : [];

    // Generate aws-exports.js content
    const awsExportsContent = generateAwsExports({
      userPoolId: USER_POOL_ID,
      userPoolClientId: USER_POOL_CLIENT_ID,
      graphqlApiUrl: GRAPHQL_API_URL,
      graphqlApiId: GRAPHQL_API_ID || '',
      region: AWS_REGION,
      appName: APP_NAME || 'SkeletonApp',
      stage: STAGE || 'dev',
      models: models
    });

    console.log('AWS exports configuration generated successfully');
    console.log('Content preview:', awsExportsContent.substring(0, 200) + '...');

    // Send success response to CloudFormation
    await sendResponse(event, 'SUCCESS', {
      ConfigGenerated: 'true',
      FilePath: './frontend/src/aws-exports.js',
      Timestamp: new Date().toISOString(),
      ContentLength: awsExportsContent.length
    });

  } catch (error) {
    console.error('Error generating AWS exports:', error);

    // Send failure response to CloudFormation
    await sendResponse(event, 'FAILED', {}, error.message || 'Unknown error occurred');
  }
};

function generateAwsExports(config) {
  const hasSubscriptions = config.models.some(model => model.hasSubscriptions);
  const hasRateLimit = config.models.some(model => model.hasRateLimit);

  return `/* eslint-disable */
// WARNING: DO NOT EDIT. This file is automatically generated by AWS CDK.
// Any changes you make will be overwritten on the next deployment.

const awsmobile = {
  "aws_project_region": "${config.region}",
  "aws_project_name": "${config.appName}",
  "aws_project_stage": "${config.stage}",
  "aws_cognito_identity_pool_id": "",
  "aws_cognito_region": "${config.region}",
  "aws_user_pools_id": "${config.userPoolId}",
  "aws_user_pools_web_client_id": "${config.userPoolClientId}",
  "oauth": {},
  "aws_cognito_username_attributes": ["email"],
  "aws_cognito_social_providers": [],
  "aws_cognito_signup_attributes": ["email"],
  "aws_cognito_mfa_configuration": "OFF",
  "aws_cognito_mfa_types": ["SMS"],
  "aws_cognito_password_protection_settings": {
    "passwordPolicyMinLength": 8,
    "passwordPolicyCharacters": []
  },
  "aws_cognito_verification_mechanisms": ["email"],
  "aws_appsync_graphqlEndpoint": "${config.graphqlApiUrl}",
  "aws_appsync_region": "${config.region}",
  "aws_appsync_authenticationType": "AMAZON_COGNITO_USER_POOLS",
  "aws_appsync_apiKey": "",
  ${hasSubscriptions ? `"aws_appsync_realTimeEndpoint": "${config.graphqlApiUrl.replace('https://', 'wss://').replace('/graphql', '/realtime')}",` : ''}
  ${hasRateLimit ? `"aws_appsync_jobCompletionEndpoint": "${config.graphqlApiUrl}",` : ''}
  "models": ${JSON.stringify(config.models, null, 2)},
  "generated_at": "${new Date().toISOString()}",
  "api_id": "${config.graphqlApiId}"
};

export default awsmobile;
`;
}

async function sendResponse(event, responseStatus, responseData, reason) {
  const responseBody = JSON.stringify({
    Status: responseStatus,
    Reason: reason || responseStatus,
    PhysicalResourceId: event.PhysicalResourceId || `aws-exports-${Date.now()}`,
    StackId: event.StackId,
    RequestId: event.RequestId,
    LogicalResourceId: event.LogicalResourceId,
    Data: responseData || {}
  });

  console.log('Sending response to CloudFormation:', responseBody);

  const parsedUrl = url.parse(event.ResponseURL);
  const options = {
    hostname: parsedUrl.hostname,
    port: 443,
    path: parsedUrl.path,
    method: 'PUT',
    headers: {
      'content-type': '',
      'content-length': Buffer.byteLength(responseBody, 'utf8')
    }
  };

  return new Promise((resolve, reject) => {
    const request = https.request(options, (response) => {
      console.log('CloudFormation response status:', response.statusCode);
      resolve();
    });

    request.on('error', (error) => {
      console.error('Error sending response to CloudFormation:', error);
      reject(error);
    });

    request.write(responseBody);
    request.end();
  });
}

